import React from "react";

const Todolist = ({ todos, DeleteTodo }) => {
  const list = todos.length ? (
    todos.map(todo => {
      return (
        <div
          className={`Collection-item ${state.todo.backlight}}`}
          key={todo.id}
        >
          <div
            className="iconSelect"
            onClick={e => {
              let Mousepoz = e.target.parentNode;
              !todo.checked ? (todo.checked = true) : (todo.checked = false);
              console.log(todo.checked);
              if (todo.checked === true) todo.backlight = "selected";
              else {
                todo.backlight = "unselected";
              }
              console.log(todo.backlight);
            }}
          ></div>
          <p>{todo.content}</p>
          <div
            className="iconDelete"
            onClick={() => {
              DeleteTodo(todo.id);
            }}
          ></div>
        </div>
      );
    })
  ) : (
    <p className="center"> You don't have todo</p>
  );
  return <div className="todos collection">{list}</div>;
};

export default Todolist;
