import React, { useState } from "react";
import Todolist from "./Todolist";
import AddinTodo from "./AddinTodo";
import Findintodo from "./Findintodo";
import Menubutton from "./Menubutton";
import "./App.css";

function App() {
  const [checkbutton, setCheckbutton] = useState("");
  const [keyWord, setKeyWord] = useState("");
  const [todolist, setTodolist] = useState({
    content: "go home",
    id: 1,
    checked: false,
    backlight: "notselected",
    status: "active"
  });

  compareWord = word => {
    setKeyWord(word);
  };

  DeleteTodo = id => {
    const todos = todolist.filter(todo => {
      return todolist.id !== id;
    });
    setTodolist(todos);
  };

  Addnewtodo = todo => {
    todo.id = Math.random();
    todo.checked = false;
    todo.backlight = "notselected";
    todo.status = "active";
    const todos = [...state.todos, todo];
    setState({
      todos
    });
  };

  HandleChange = e => {
    todolistState([
      ...todolist,
      {
        content: e.target.value
      }
    ]);
  };

  handleCheckbox = id => {
    const todos = [...todolist];
    todos.forEach(todo => {
      if (todo.id === id) {
        if (todo.checked === false) {
          {
            checked: true;
            backlight: true;
          }
        } else {
          checked: false;
          acklight: "unselected";
        }
      }
    });
    todolistState({
      todos
    });
  };

  let FilteredTodo = todolist.filter(todo => {
    //console.log(state.keyWord);

    return todo.content.includes(state.keyWord);
  });

  return (
    <div className="Allpage">
      <div className="container">
        <div className="header">
          <h1>Todos</h1>
        </div>
      </div>
      <div className="container">
        <Findintodo compareWord={compareWord} />
        <Todolist
          todos={FilteredTodo}
          DeleteTodo={DeleteTodo}
          handleCheckbox={handleCheckbox}
        />

        <AddinTodo Addnewtodo={Addnewtodo} />
        <Menubutton
          complitedButton={complitedButton}
          activeButton={activeButton}
          allButton={allButton}
        />
      </div>
    </div>
  );
}

